void Map::offsetMap(int mod) {//1 - up, 2 - down, 3 - left, 4 - right
    SDL_Point tempPos;

    for (int i = 0; i < PIXEL_PER_WIDTH; i++)
        for (int j = 0; j < PIXEL_PER_HEIGHT; j++) {
            tempPos = pixels[i][j].getPos();
            if (mod == 1 && pixels[0][0].getPosY() <= -5) {
                tempPos.y += 5;
            }
            if (mod == 2) {
                tempPos.y -= 5;
            }
            if (mod == 3) {
                tempPos.x += 5;
            }
            if (mod == 4) {
                tempPos.x -= 5;
            }
            pixels[i][j].setPos(tempPos.x, tempPos.y);
        }
}

