    getMatrixFromFile();

    for (int i = 0; i < PIXEL_PER_HEIGHT; i++)
        for (int j = 0; j < PIXEL_PER_WIDTH; j++) {
            pixels[i][j].setAltitude(altMatrix[j][i]);
            pixels[i][j].setPos(i * PIXEL_WIDTH, j * PIXEL_HEIGHT);
        }
